mapper.update:
update ccairbag_user_browse_history
set browse_time = #{browseTime}
where id = #{id}

mapper.delete:

delete from ccairbag_user_browse_history
where user_id = #{userId}
and id < #{thresholdId}

mapper.select:
select id, user_id, product_id, product_name, shop_id, shop_name, price, pic, imgs, browse_time
from ccairbag_user_browse_history
where user_id = #{userId}
  and product_id = #{productId}
order by browse_time desc
    limit 1

mapper.delete:
